mapper.update:
UPDATE USER
    SET add_num = add_num + 1
WHERE user_nickname = #{userName}

UPDATE USER
SET added_num = added_num + 1
WHERE user_nickname = #{userName}

UPDATE USER
SET add_num = add_num - 1
WHERE user_nickname = #{userName}

UPDATE USER
SET added_num = added_num - 1
WHERE user_nickname = #{userName}
